<?php// Inclusionrequire_once dirname(__FILE__).'/init.php';$url	= GcLib\Request::getUrl(false);$router	= new GcLib\Router($url);if (isset($routes)){	$router->addRoutes($routes);}$router->matcher();// Front controller$controllerName	= ucfirst(GcLib\Request::getParam('controller'));$actionName		= ucfirst(GcLib\Request::getParam('action'));$frontController = new GcLib\Controller\Front();$frontController->setControllerName($controllerName);$frontController->setActionName($actionName);//\var_dump($controllerName, $actionName, PATH_ROOT); exit;$frontController->dispatch();